import React, { useContext, useState } from 'react';
import { ShopContext } from '../../Context/ShopContext';
import ''

export const Checkout = () => {
  const { cartItems, all_product, getTotalCartAmount, getTotalCartItems } = useContext(ShopContext);
  const [paymentMode, setPaymentMode] = useState('cod');

  const handlePaymentChange = (e) => {
    setPaymentMode(e.target.value);
  };

  return (
    <div style={{ maxWidth: 500, margin: '2rem auto', padding: '2rem', border: '1px solid #ccc', borderRadius: 8 }}>
      <h2>Order Checkout</h2>
      <div style={{ marginBottom: '1rem' }}>
        <strong>Number of Items:</strong> {getTotalCartItems()}
      </div>
      <div style={{ marginBottom: '1rem' }}>
        <strong>Total Amount:</strong> ${getTotalCartAmount()}
      </div>
      <div style={{ marginBottom: '1rem' }}>
        <strong>Order Summary:</strong>
        <ul>
          {all_product.filter(e => cartItems[e.id] > 0).map(e => (
            <li key={e.id}>
              {e.name} x {cartItems[e.id]} = ${e.new_price * cartItems[e.id]}
            </li>
          ))}
        </ul>
      </div>
      <div style={{ marginBottom: '1rem' }}>
        <strong>Mode of Payment:</strong>
        <div>
          <label>
            <input
              type="radio"
              value="cod"
              checked={paymentMode === 'cod'}
              onChange={handlePaymentChange}
            />
            Cash on Delivery
          </label>
        </div>
        <div>
          <label>
            <input
              type="radio"
              value="card"
              checked={paymentMode === 'card'}
              onChange={handlePaymentChange}
            />
            Credit/Debit Card
          </label>
        </div>
        <div>
          <label>
            <input
              type="radio"
              value="upi"
              checked={paymentMode === 'upi'}
              onChange={handlePaymentChange}
            />
            UPI
          </label>
        </div>
      </div>
      <button style={{ padding: '0.5rem 2rem', fontSize: 16 }}>Place Order</button>
    </div>
  );
};
